import './App.css';
import React, { Component } from 'react'


class Summary extends Component {
  constructor(props) {
   super(props)
    /*
    this.state = {
      list: data
      //name: list.author,
    }
    */
  }

  render() {
    return (
      <div className="text-box">
          <textarea className = "text-area" cols="60">"RPG and sequel to The Witcher 2 (2011), 
              The Witcher 3 follows witcher Geralt of Rivia as he seeks out his former lover and his 
               young subject while intermingling with the political workings of the wartorn Northern Kingdoms. 
               Geralt has to fight monsters and deal with people of all sorts in order to solve complex 
               problems and settle contentious disputes, each ranging from the 
               personal to the world-changing."</textarea>
      </div>
    );
  }

}

export default Summary;
