import logo from './logo.svg';
import './App.css';
import axios from 'axios'
import React, { Component } from 'react'
//import data from './express'

const data = axios({
  url: "https://api.igdb.com/v4/games",
  method: 'POST',
  headers: {
    'Accept': 'application/json',
    'Client-ID': '0v770zzwczmv4jqsycklt3c25xknad',
    'Authorization': 'Bearer fyq4k5moya940jn9wyl18bc15ugnk8' //alterar a cada hora

    //'Authorization: mfj5q3fwcv5nmqj5oxkiezrn52vnz8', 
    //'user-key': '2091bb9b8d9a4f5fd58e960a35476cad'
  },
  data: "fields *; where id = 1942;"

  //"fields achievement_icon,category,checksum,created_at,description,external_id,game,language,locked_achievement_icon,name,owners,owners_percentage,rank,slug,tags,updated_at;"

})
  .then(response => {
    return response.data;
  })
  .catch(err => {
    console.error(err);
  })


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      list: data.then(function(data){
        return data
      }),
      //name: list.author,
    }

  }

  render() {
    return (
      <div className="App">
        {this.state.data.map(item =>
          <div key={item.id} className="text-box">
            <div className="data-area">
              <span>{' ' + item.name}</span>
            </div>
          </div>
        )}
      </div>
    );
  }

}

export default App;
