import './App.css';
import React from 'react'
import axios from 'axios'

async function  getData() {
  let data = axios.get('http://localhost:4000/')
    .then(function (response) {
      // handle success
      return response.data
      //console.log(response.data)
    })
    .catch(function (error) {
      // handle error
      console.log(error);
    })

  const list = {}

  data.then(function (data) {

    list.name = data.name
    list.rating = data.rating
    list.summary = data.summary

  })
  return list
}

const list = getData()


class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      list: list,
    }
    console.log(list)
  }

  render() {
    return (
      <div>
          <div className="App">
            <h1>{list.name}</h1>
          </div>


      </div>
    );
  }
}

export default App;
