import './App.css';
import React from 'react'
import axios from 'axios'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      rating: 0,
      name: '',
      url: ''
    };
  }
  getData = () => {
    const data = this //seta o this de uma const para acessa-lo posteriormente dentro da promise  

    axios.get('http://localhost:4000/')
      .then(function (response) {

        const name = response.data.name
        const url = response.data.url
        data.setState({ name: name, url: url })
      })
      .catch(function (error) {

        console.log(error);
      })
  }

  render() {

    return (

      <div>
        <div className="App">
          <h1>{/*this.state.name*/"The Whitcher 3"}</h1>
          <img src={this.state.url}></img>
          <button type="button" onClick={this.getData}>teste</button>
        </div>
      </div>

    );
  }
}

export default App;
