import './App.css';
import React from 'react'
import axios from 'axios'
import Summary from './Summary'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      rating: 0,
      name: '',
      url: '',
      summary: '',
    };
  }
  getData = () => {
    const data = this //seta o this de uma const para acessa-lo posteriormente dentro da promise  

    axios.get('http://localhost:4000/')
      .then(function (response) {

        const name = response.data.name
        const url = response.data.url
        const rating = response.data.rating
        const summary = response.data.summary
        data.setState({ name: name, url: url, rating: rating, summary: summary })
        //console.log(summary)
      })
      .catch(function (error) {

        console.log(error);
      })
  }

  render() {
    const summaryTextArea = this.state.summary
    const teste = 'teste'
    console.log(summaryTextArea)
    return (
      <div>

        <div className="App">
          <div className="elipse"><p>{Math.round(this.state.rating)}</p></div>
          <h1>{this.state.name}</h1>
          <img src={this.state.url}></img>
          <button type="button" onClick={this.getData}>teste</button> 
        </div>  
        <Summary summary= {teste}></Summary>
      </div>


    );
  }
}

export default App;
