import './App.css';
import React from 'react'
import axios from 'axios'
import Summary from './Summary'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      rating: 0,
      name: '',
      url: '',
      summary: '',
      genres: [],
      screenshots: [],
    };
  }
  getData = () => {
    const data = this //seta o this de uma const para acessa-lo posteriormente dentro da promise  

    axios.get('http://localhost:4000/')
      .then(function (response) {

        const name = response.data.name
        const url = response.data.url
        const rating = response.data.rating
        const summary = response.data.summary
        const genres = response.data.genres
        const screenshots = response.data.screenshots
        data.setState({ name: name, url: url, rating: rating, summary: summary, genres: genres, screenshots: screenshots })
        //console.log(screenshots)
      })
      .catch(function (error) {

        console.log(error);
      })
  }

  render() {
    let genresRow = []
    for (let i = 0; i < this.state.genres.length; i++) {
      genresRow.push(<a key={i}> {this.state.genres[i]} | </a>)
    }

    let screenshotsData = []

    if (this.state.screenshots.length > 0) {
      for (let i = 0; i < 3; i++) {
        screenshotsData.push(<div className="ss-img" key={i}><img src={this.state.screenshots[i]}></img></div>)
      } 
    } else{ console.log(this.state.screenshots)}

    return (
      <div>
        <div className="App">
          <div className="elipse"><p>{Math.round(this.state.rating)}</p></div>
          <h1>{this.state.name}</h1>
          <img src={this.state.url}></img>
          <button type="button" onClick={this.getData}>teste</button>
          <span className="genres"> Genres:
              {genresRow}
          </span>
        </div>
        <Summary summary={this.state.summary}></Summary>
        <p className="text-screenshots">Screenshots:</p>
        <div className="screen-shots">{screenshotsData}</div>
      </div>
    );
  }
}

export default App;
