import axios from 'axios'
import React, { Component } from 'react'

const list = axios({
  url: "https://api.igdb.com/v4/games",
  method: 'POST',
  headers: {
    'Accept': 'application/json',
    'Client-ID': '0v770zzwczmv4jqsycklt3c25xknad',
    'Authorization': 'Bearer fyq4k5moya940jn9wyl18bc15ugnk8' //alterar a cada hora

    //'Authorization: mfj5q3fwcv5nmqj5oxkiezrn52vnz8', 
    //'user-key': '2091bb9b8d9a4f5fd58e960a35476cad'
  },
  data: "fields *; where id = 1942;"

  //"fields achievement_icon,category,checksum,created_at,description,external_id,game,language,locked_achievement_icon,name,owners,owners_percentage,rank,slug,tags,updated_at;"

})
  .then(response => {
    console.log(response.data);
  })
  .catch(err => {
    console.error(err);
    console.log("teste")
  })


//lista teste
const listTwo = [
  {
    title: 'React',
    url: 'https://facebook.github.io/react/',
    author: 'Jordan Walke',
    num_comments: 3,
    points: 4,
    objectID: 0,
  },
  {
    title: 'Redux',
    url: 'https://github.com/reactjs/redux',
    author: 'Dan Abramov, Andrew Clark',
    num_comments: 2,
    points: 5,
    objectID: 1,
  },
  {
    title: 'NodeJS',
    url: 'https://npmjs.com',
    author: 'Ryan Dahl',
    num_comments: 2,
    points: 5,
    objectID: 2,
  },
];


class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      list: listTwo,
      //name: list.author,
    }

  }

  render() {
    return (
      <div className="App">
        {this.state.list.map(item =>
          <div key={item.objectID} className="text-box">
            <div className="data-area">
              <span>{' ' + item.title}</span>
            </div>
          </div>
        )}
      </div>
    );
  }

}

export default App;
