    console.log(`A Aplicação esta rodando na porta: ${PORT}`)
})

const data = axios({
    url: "https://api.igdb.com/v4/games",
    method: 'POST',
    headers: {
        'Accept': 'application/json',
        'Client-ID': '0v770zzwczmv4jqsycklt3c25xknad',
